import React, { useState } from 'react'
import AsyncStorage from '@react-native-async-storage/async-storage';

const CURRENT_USER_KEY = 'current-user'
const GlobalContext = React.createContext()

const INITAL_STATE = {
    user: AsyncStorage.getItem(CURRENT_USER_KEY) || '{}'
}

const GlobalStore = ({children}) => {
    const [state, setState] = useState(AsyncStorage.getItem(CURRENT_USER_KEY) || '{}')

    const handleUserChange = (newUser) => {
        setUser(newUser)
        if (newUser && newUser.id) AsyncStorage.setItem(CURRENT_USER_KEY, JSON.stringify(newUser))
        else AsyncStorage.removeItem(CURRENT_USER_KEY) 
    }

    const isAuthenticated = () => user && user.id

    const getStateKey = (key) => {

    }

    return (
        <GlobalContext.Provider
            value={{
                user,
                isAuthenticated,
                onUserChange: handleUserChange,
            }}
        >
            {children}
        </GlobalContext.Provider>
    )
}

export {GlobalStore, GlobalContext};
